import React, { Component } from "react";
import "./App.css";
import { Button } from "react-bootstrap";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import { Form } from "react-bootstrap";
import { Image } from "react-bootstrap";
import { InputGroup } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from "@fortawesome/fontawesome-svg-core";
import { faUser, faKey } from "@fortawesome/free-solid-svg-icons";
import "./components/inputs-form/InputForm.component"
import InputForm from "./components/inputs-form/InputForm.component";

class App extends Component {
  constructor() {
    super();

    this.state={
      width:window.innerWidth
    }

  }

  componentDidMount(){
    window.addEventListener("resize", this.resize.bind(this));
    this.resize();
  }

  resize() {
    this.setState({width: window.innerWidth},()=>{
      console.log(this.state.width)
    });
}

  render() {
    return (
      <div className="login">
        <Container   >
          <Row>
            <Col className="vertcen">
              <Image
                src={require("./rabbit.png")}
                style={{ height: "300px" }}
                fluid
              />
              <Form className="mt-3 ">
                <Form.Group>
                  <InputForm type="email" placeholder="user" icon={"faUser"}/>
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faUser} />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <Form.Control
                      type="email"
                      placeholder="User"
                      aria-describedby="inputGroupPrepend"
                      required
                    />
                  </InputGroup>
                </Form.Group>
                <Form.Group>
                  <InputGroup>
                    <InputGroup.Prepend>
                      <InputGroup.Text>
                        <FontAwesomeIcon icon={faKey} />
                      </InputGroup.Text>
                    </InputGroup.Prepend>
                    <Form.Control
                      type="password"
                      placeholder="Password"
                      aria-describedby="inputGroupPrepend"
                      required
                    />
                  </InputGroup>
                </Form.Group>
                <Button  className="w-100" type="submit" style={{
                  backgroundColor:"#D65DB1",
                  borderColor:"#ffffff"
                }}>
                  Log In
                </Button>
                <Button  className="w-100 mt-2"  style={{
                  backgroundColor:"#ffffff",
                  borderColor:"#D65DB1",
                  color:"#D65DB1	"
                }}>
                  Sign Up
                </Button>
              </Form>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
